    }

    return(
        <div>
            <div className="list-all-text-content">
                <div >
                    <p>Date:{duedate}</p>
                    <p>Name:{name}</p>
                    <p>Description:{description}</p>
                    <p>Assigned:{assignedto}</p>
                    <p>Status:{status}</p>
                </div>
            </div>
