import React, { useState, useEffect } from 'react';
import './DesktopInput.css';
import { v4 } from 'uuid';

const DesktopInput =( {add} ) => {


    const [date, setDate] = useState('')
    function dateChange(e) {
        setDate(e.target.value)
    }

    const [name, setName] = useState('')
    function nameChange(e) {
        setName(e.target.value)
    }

    const [description, setDescription] = useState('')
    function descriptionChange(e) {
        setDescription(e.target.value)
    }

    const [assigned, setAssigned] = useState('')
    function assignedChange(e) {
        setAssigned(e.target.value)
    }

    const [status, setStatus] = useState('')
    function statusChange(e) {
        setStatus(e.target.value)
    }

    console.log(date, name, description, assigned, status)

    function addList() {
        add(function(prev) {
            return [...prev, {
            id: v4(),
            date,
            name,
            description,
            assigned,
            status,
            }]
        })
    }


    async function getDateFetch(){
        const res = await fetch ("http://localhost:8080/todolist")
        const data = await res.json()
        console.log(data)
    }
    useEffect(() => {
        console.log(getDateFetch)
    }, [])

    return (
        <div className="desktop-text-container">

            <div id="desktop-text-area" className="desktop-text-area">
                    <div className="desktopDuedate">
                        <label>Due Date:</label>
                        <br />
                        <input type="date" value={date} onChange={dateChange} required />
                    </div>

                    <div className="desktopName">
                        <label>Name:</label>
                        <br />
                        <input type="text" value={name} onChange={nameChange} required />
                    </div>

                    <div className="desktopDescription">
                       <label>Description:</label>
                        <br />
                        <input type="text" value={description} onChange={descriptionChange} required />
                    </div>

                    <div className="desktopAssignedto">
                        <label>Assigned:</label>
                        <br />
                        <input type="text" value={assigned} onChange={assignedChange} required />
                    </div>

                    <div className="desktopStatus">
                        <label>Status:</label>
                        <br />
                        <input type="text" value={status} onChange={statusChange} required />
                    </div>

                    <div className="desktop-text-button">
                        <div className="desk-submit-button">
                            <button className="desktop-show-data" type="reset">Show List</button>
                            <button className="desktop-submit" type="submit" onClick={addList}>Submit</button>
                        </div>
                     </div>
             </div>

        </div>
    )
}

export default DesktopInput;
