import React, { useState } from 'react';
import './DesktopInput.css'

const DesktopInput =() => {

    const [date, setDate] = useState('')
    function dateChange(e) {
        setDate(e.target.vaule)
    }
    // const [date, setDate] = useState('')
    // function dateChange(e) {
    //     setDate(e.target.vaule)
    // }
    // const [date, setDate] = useState('')
    // function dateChange(e) {
    //     setDate(e.target.vaule)
    // }

    return (
        <div className="desktop-text-container">

            <div id="desktop-text-area" className="desktop-text-area">

                <form action="http://localhost:8080/" method="POST">
                    <div className="desktopDuedate">
                        <label>Due Date:</label>
                        <br />
                        <input type="date" value={date} onChange={dateChange} required />
                    </div>

                    <div className="desktopName">
                        <label>Name:</label>
                        <br />
                        <input type="text" name="desktopName" required />
                    </div>

                    <div className="desktopDescription">
                       <label for="desktopDescription'">Description:</label>
                        <br />
                        <input type="text" name="desktopDescription" required />
                    </div>

                    <div className="desktopAssignedto">
                        <label for="desktopAssignedto'">Assigned:</label>
                        <br />
                        <input type="text" name="desktopAssignedto" required />
                    </div>

                    <div className="desktopStatus">
                        <label for="desktopStatus'">Status:</label>
                        <br />
                        <input type="text" name="desktopStatus" required />
                    </div>

                    <div className="desktop-text-button">
                        <div className="desk-submit-button">
                            <button className="desktop-show-data" type="reset">Show List</button>
                            <button className="desktop-submit" type="submit">Submit</button>
                        </div>
                     </div>
                 </form>
             </div>

        </div>
    )
}

export default DesktopInput;
